package Ch02;

public class C02음수 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 컴퓨터(CPU)는 가산회로에 의해 덧셈처리를 할 수 있다.	(o)
		// 컴퓨터(CPU)는 뺄셈처리 할 수 있다.	(x)
		// 실제로 뺄셈의 결과를 가져오는 이유는 보수개념을 도입했기떄문이다.
		
		
		//믄제
		// 10 진수   -> 2진수
		// 96		-> 01100000
		// -96		-> 10100000
		// 31		-> 00011111
		// -31		-> 11100001
		
		
		// 2진수		-> 10진수
		// 10101111 -> 81
		// 00110101 ->
		// 11001100 ->
		// 10101010
		
		
		
		
		System.out.println((byte)0b10101111);
	}

}
